const mongoose = require('mongoose')

mongoose.connect('mongodb://localhost/local',{ useNewUrlParser: true, useUnifiedTopology: true })

const userSchema = new mongoose.Schema({
    first_name: String,
    last_name: String,
    email: String,
    zipcode: String,
    uid: String 
})

const user = mongoose.model('user', userSchema)

